import React from "react";
import Navigation from "./navigation";
import ImageLink from "./imageLink";
import Footer from "./footer";
import * as faceapi from 'face-api.js';

function App() {
  const imgRef = React.useRef();
  const canvasRef = React.useRef();

  const handleImage = async () => {
    const detections = await faceapi.detectAllFaces(
      imgRef.current,
      new faceapi.TinyFaceDetectorOptions()
    );
    setFaces(detections.map((d) => Object.values(d.box)));
  };

  const enter = () => {
    const ctx = canvasRef.current.getContext("2d");
    ctx.lineWidth = 5;
    ctx.strokeStyle = "yellow";
    faces.map((face) => ctx.strokeRect(...face));
  };

  React.useEffect(() => {
    const loadModels = () => {
      Promise.all([
        faceapi.nets.tinyFaceDetector.loadFromUri("/models"),
        faceapi.nets.faceLandmark68Net.loadFromUri("/models"),
        faceapi.nets.faceExpressionNet.loadFromUri("/models"),
      ])
        .then(console.log(handleImage))
        .catch((e) => console.log(e));
    };

    imgRef.current && loadModels();
  }, []);

  function imgLink(link){
   console.log(link);
  }
  return (
    <div className="App">
      <Navigation />
      <ImageLink fetchImg={imgLink}/>
      <img ref={imgRef} src="https://www.google.com/url?sa=i&url=https%3A%2F%2Funsplash.com%2Fs%2Fphotos%2Frandom-people&psig=AOvVaw3E_RufBQUynHAMT_xUVE1B&ust=1673838076248000&source=images&cd=vfe&ved=0CA8QjRxqFwoTCIjCjPHKyPwCFQAAAAAdAAAAABAE" 
      alt="image" 
      style={{width: '500px', height: '500px'}}
      />
      <canvas ref={canvasRef} style={{width: '500px', height: '500px'}}></canvas>
      <Footer />
      </div>
  );
}

export default App;
